import React from 'react';
import Navbar from "../Navbar/Navbar";
import SearchForm from "../SearchForm/SearchForm";
import "./Header.css";

const Header = () => {
  return (
    <div className='holder'>
        <header className='header'>
            <Navbar />
            <div className='header-content'>
                <h2 className='header-title text-capitalize'>Discover Your Next Favorite Read</h2>
                <p className='header-text fs-18 fw-3'>Welcome to Literature-Map, your literary compass in the vast world of books! Whether you're seeking your next favorite author or exploring new literary territories, Literature-Map is here to guide you on your literary journey. Our mission is simple yet profound: to make book recommendations easy, enjoyable, and personalized for every reader. Join us in celebrating the joy of reading and let Literature-Map be your compass in the world of words.</p>

                <SearchForm className="search-form" />
            </div>
        </header>
    </div>
  )
}

export default Header;
