import React from 'react';
import "./About.css";
import aboutImg from "../../images/about-img.jpeg";

const About = () => {
  return (
    <section className='about'>
      <div className='container'>
        <div className='section-title'>
          <h2>About</h2>
        </div>

        <div className='about-content grid'>
          <div className='about-img'>
            <img src={aboutImg} alt="About Literature-Map" />
          </div>
          <div className='about-text'>
            <h2 className='about-title fs-26 ls-1'>About Literature-Map</h2>
            <p className='fs-17'>Welcome to Literature-Map, your literary compass in the vast world of books! At Literature-Map, we are passionate about connecting readers with their next great read. Whether you're seeking your next favorite author or exploring new literary territories, Literature-Map is here to guide you on your literary journey.</p>
            <p className='fs-17'>Using advanced algorithms and data analysis, Literature-Map creates personalized book recommendations based on your literary preferences. Simply input an author or book you love, and watch as our interactive map reveals a constellation of related authors and works. With Literature-Map, exploring new books has never been more intuitive or exciting.</p>
          </div>
        </div>
      </div>
    </section>
  )
}

export default About;
