import React, { useState, useEffect } from 'react';
import "./ToRead.css";
const ToRead = () => {
  const [data, setData] = useState({
    todo: [],
    completed: []
  });

  // Icons in SVG format
  const removeSVG = (
    <svg
      version="1.1"
      xmlns="http://www.w3.org/2000/svg"
      xmlnsXlink="http://www.w3.org/1999/xlink"
      x="0px"
      y="0px"
      viewBox="0 0 22 22"
      style={{ enableBackground: 'new 0 0 22 22' }}
      xmlSpace="preserve"
    >
      <rect className="noFill" width="22" height="22" />
      <g>
        <g>
          <path className="fill" d="M16.1,3.6h-1.9V3.3c0-1.3-1-2.3-2.3-2.3h-1.7c-1.3,0-2.3,1-2.3,2.3v0.2H5.9c-1.3,0-2.3,1-2.3,2.3v1.3c0,0.5,0.4,0.9,0.9,1v10.5c0,1.3,1,2.3,2.3,2.3h8.5c1.3,0,2.3-1,2.3-2.3V8.2c0.5-0.1,0.9-0.5,0.9-1V5.9C18.4,4.6,17.4,3.6,16.1,3.6z M9.1,3.3c0-0.6,0.5-1.1,1.1-1.1h1.7c0.6,0,1.1,0.5,1.1,1.1v0.2H9.1V3.3z M16.3,18.7c0,0.6-0.5,1.1-1.1,1.1H6.7c-0.6,0-1.1-0.5-1.1-1.1V8.2h10.6V18.7z M17.2,7H4.8V5.9c0-0.6,0.5-1.1,1.1-1.1h10.2c0.6,0,1.1,0.5,1.1,1.1V7z" />
        </g>
        <g>
          <g>
            <path className="fill" d="M11,18c-0.4,0-0.6-0.3-0.6-0.6v-6.8c0-0.4,0.3-0.6,0.6-0.6s0.6,0.3,0.6,0.6v6.8C11.6,17.7,11.4,18,11,18z" />
          </g>
          <g>
            <path className="fill" d="M8,18c-0.4,0-0.6-0.3-0.6-0.6v-6.8c0-0.4,0.3-0.6,0.6-0.6c0.4,0,0.6,0.3,0.6,0.6v6.8C8.7,17.7,8.4,18,8,18z" />
          </g>
          <g>
            <path className="fill" d="M14,18c-0.4,0-0.6-0.3-0.6-0.6v-6.8c0-0.4,0.3-0.6,0.6-0.6c0.4,0,0.6,0.3,0.6,0.6v6.8C14.6,17.7,14.3,18,14,18z" />
          </g>
        </g>
      </g>
    </svg>
  );

  const completeSVG = (
    <svg
      version="1.1"
      xmlns="http://www.w3.org/2000/svg"
      xmlnsXlink="http://www.w3.org/1999/xlink"
      x="0px"
      y="0px"
      viewBox="0 0 22 22"
      style={{ enableBackground: 'new 0 0 22 22' }}
      xmlSpace="preserve"
    >
      <rect y="0" className="noFill" width="22" height="22" />
      <g>
        <path className="fill" d="M9.7,14.4L9.7,14.4c-0.2,0-0.4-0.1-0.5-0.2l-2.7-2.7c-0.3-0.3-0.3-0.8,0-1.1s0.8-0.3,1.1,0l2.1,2.1l4.8-4.8c0.3-0.3,0.8-0.3,1.1,0s0.3,0.8,0,1.1l-5.3,5.3C10.1,14.3,9.9,14.4,9.7,14.4z" />
      </g>
    </svg>
  );

  useEffect(() => {
    const storedData = localStorage.getItem('todoList');
    if (storedData) {
      setData(JSON.parse(storedData));
    }
  }, []);

  const addItem = (value) => {
    addItemToDOM(value);
    const newData = { ...data, todo: [...data.todo, value] };
    setData(newData);
    dataObjectUpdated(newData);
  };

  const renderTodoList = () => {
    if (!data.todo.length && !data.completed.length) return;

    return (
      <>
        {data.todo.map((value) => (
          addItemToDOM(value)
        ))}
        {data.completed.map((value) => (
          addItemToDOM(value, true)
        ))}
      </>
    );
  };

  const dataObjectUpdated = (updatedData) => {
    localStorage.setItem('todoList', JSON.stringify(updatedData));
  };

  const removeItem = (value, id) => {
    const updatedData = { ...data };
    if (id === 'todo') {
      updatedData.todo = updatedData.todo.filter(item => item !== value);
    } else {
      updatedData.completed = updatedData.completed.filter(item => item !== value);
    }
    setData(updatedData);
    dataObjectUpdated(updatedData);
  };

  const completeItem = (value, id) => {
    const updatedData = { ...data };
    if (id === 'todo') {
      updatedData.todo = updatedData.todo.filter(item => item !== value);
      updatedData.completed = [...updatedData.completed, value];
    } else {
      updatedData.completed = updatedData.completed.filter(item => item !== value);
      updatedData.todo = [...updatedData.todo, value];
    }
    setData(updatedData);
    dataObjectUpdated(updatedData);
  };

  const addItemToDOM = (text, completed = false) => {
    const list = completed ? document.getElementById('completed') : document.getElementById('todo');

    const item = document.createElement('li');
    item.innerText = text;

    const buttons = document.createElement('div');
    buttons.classList.add('buttons');

    const remove = document.createElement('button');
    remove.classList.add('remove');
    remove.innerHTML = removeSVG;

    remove.addEventListener('click', () => removeItem(text, completed ? 'completed' : 'todo'));

    const complete = document.createElement('button');
    complete.classList.add('complete');
    complete.innerHTML = completeSVG;

    complete.addEventListener('click', () => completeItem(text, completed ? 'completed' : 'todo'));

    buttons.appendChild(remove);
    buttons.appendChild(complete);
    item.appendChild(buttons);

    list.insertBefore(item, list.childNodes[0]);
  };

  return (
    <div>
      <input type="text" id="item" />
      <button id="add" onClick={() => addItem(document.getElementById('item').value)}>
        Add
      </button>
      <ul id="todo"></ul>
      <ul id="completed"></ul>
      {renderTodoList()}
    </div>
  );
};

export default ToRead;
