import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { AppProvider } from './context'; // Assuming correct path to AppProvider
import './index.css';
import Home from './pages/Home/Home';
import About from './pages/About/About';
import BookList from './components/BookList/BookList';
import BookDetails from './components/BookDetails/BookDetails';
import ToReadList from './pages/ToReadList/ToReadList'; // Import the ToReadList component

const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(
  <AppProvider>
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/book" element={<BookList />} />
        <Route path="/book/:id" element={<BookDetails />} />
        <Route path="/to-read" element={<ToReadList />} /> {/* Route for ToReadList */}
      </Routes>
    </BrowserRouter>
  </AppProvider>
);
